#' Launch grid search with given algorithm and parameters.
#'
#' @param algorithm  name of algorithm to use in grid search (gbm, drf, kmeans, glm, deeplearning)
#' @param grid_id  optional id for resulting grid search, if it is not specified then it is autogenerated
#' @param ...  arguments describing parameters to use with algorithm (i.e., x, y, training_frame). 
#'        Look at the specifici algorithm - h2o.gbm, h2o.glm, h2o.kmeans, h2o.deepLearning
#' @param hyper_params  list of hyper parameters (i.e., \core{list(ntrees=c(1,2), max_depth=c(5,7))})
#' @param 
#' @examples
#' library(h2o) 
#' localH2O <- h2o.init()
#' iris.hex <- as.h2o(iris)
#' grid <- h2o.grid("gbm", x=c(1:4), y = 5, training_frame=hex, hyper_params=list("_ntrees"=c(1,10,30)))
#' @export
h2o.grid <- function(algorithm,
                     grid_id,
                     ...,
                     hyper_params = list(),
                     conn = h2o.getConnection())
{
  # Extract parameters
  dots <- list(...)
  params <- .h2o.prepareModelParameters(algo = algorithm, params = dots)
  # FIXME: rename x to ignore, y to response

  # Validation of model parameters
  .key.validate(params$key_value)
  # Get model builder parameters for this model
  allParams <- .h2o.getModelParameters(algo = algorithm)
  # Verify the parameters
  params <- lapply(params, function(x) { if(is.integer(x)) x <- as.numeric(x); x })
  params <- .h2o.checkModelParameters(algo = algorithm, allParams = allParams, params = params)
  # TODO: Validate hyper parameters

  # Append grid parameters
  params$hyper_parameters <- toJSON(hyper_params)

  # Append grid_id if it is specified
  if (!missing(grid_id)) params$grid_id <- grid_id

  # Trigger grid search job
  res <- .h2o.__remoteSend(conn, .h2o.__GRID(algorithm), h2oRestApiVersion = 99, .params = params, method = "POST")
  grid_id <- res$job$dest$name
  job_key <- res$job$key$name
  # Wait for grid job to finish
  .h2o.__waitOnJob(conn, job_key)

  h2o.getGrid(grid_id = grid_id, conn = conn)
}

#' Get a grid object from H2O distributed K/V store.
#'
#' @param grid_id  ID of existing grid object to fetch
#' @examples
#' librar(h2o)
#' localH2O <- h2o.init()
#' grid <- h2o.getGrid("GBM_grid_num42")
#' @export
h2o.getGrid <- function(grid_id, conn = h2o.getConnection(), linkToGC = FALSE) {
  json <- .h2o.__remoteSend(conn, method = "GET", h2oRestApiVersion = 99, .h2o.__GRIDS(grid_id))

  class <- "H2OGrid"
  grid_id <- json$grid_id$name
  model_ids <- lapply(json$model_ids, function(model_id) { model_id$name })

  new(class, grid_id = grid_id, model_ids = model_ids)
}

